import React from 'react';
import PropTypes from 'prop-types';


// 수많은 리액트 컴포넌트의 기능들을 사용하기 위해서 app이란 컴포넌트를 같은 생태계에 넣어주는 개념
// state는 오브젝트이다. 컴포넌트의 데이터를 넣을 공간으로 이해
//render안에 데이터 사용을 위해 state를 사용할 때에는 컴포넌트 함수 형태로 사용해야 한다. 아래 참조

class clock extends React.Component {
 constructor (props) {
   super (props);
   this.state = {current_date : new Date()};
 }

  render() {
    return (
      <div>
        <h1>The number is {this.state.date}</h1>
        <button>add</button>
        <button>minus</button>
      </div>
    );
  }
}

export default clock;
