import React from 'react';


// 외부 폴더에 컴포넌트 파일을 만들 수도 있지만 학습 편의성 위해서 내부에
// Movie_list 컴포넌트의 propos를 name으로 만들어서 사용하는 
function Movie_list({name}){
  console.log({name});
  return (
      <h4>My favorite movie is  {name} </h4>  //jsx +props를 통해 정적 데이터와 동적 데이터 보여줌
  )
}


// 컴포넌트들을 호출해서 조립
function App() {
  return (
    <div>
      <h1>react.js hello world</h1>
      <Movie_list name = "batman" />  
      <Movie_list name = "ironman" />
      <Movie_list name = "flash" />
      <Movie_list name = "superman" />
    </div>
  )
}

export default App;
